void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float s = 0.0, n = 0.0, v = 0.0;

    s += textureLod(iChannel0, uv, 0.0).r;

    n = textureLod(iChannel0, (fragCoord.xy + vec2(0, -1))/iResolution.xy, 0.0).r;
    v = max(v, n);
    s += n;

    n = textureLod(iChannel0, (fragCoord.xy + vec2(-1,  0))/iResolution.xy, 0.0).r;
    v = max(v, n);
    s += n;

    n = textureLod(iChannel0, (fragCoord.xy + vec2( 0, 1))/iResolution.xy, 0.0).r;
    v = max(v, n);
    s += n;

    n = textureLod(iChannel0, (fragCoord.xy + vec2( 1,  0))/iResolution.xy, 0.0).r;
    v = max(v, n);
    s += n;

    n = textureLod(iChannel0, (fragCoord.xy + vec2(-1, -1))/iResolution.xy, 0.0).r;
    v = max(v, n);
    s += n;

    n = textureLod(iChannel0, (fragCoord.xy + vec2(-1,  1))/iResolution.xy, 0.0).r;
    v = max(v, n);
    s += n;

    n = textureLod(iChannel0, (fragCoord.xy + vec2( 1, -1))/iResolution.xy, 0.0).r;
    v = max(v, n);
    s += n;

    n = textureLod(iChannel0, (fragCoord.xy + vec2( 1,  1))/iResolution.xy, 0.0).r;
    v = max(v, n);
    s += n;

    float n1 = 2.0/s;
    float n2 = v;
    float n3 = v * 9.0;
    float m = s / v;
    float a = 0.5;
    float b = 1.0;

    s = gielisFormula(uv * 2.0 - 1.0, vec3(n1, n2, n3), vec3(a, b, m));

    /////////////////////
    if (iFrame < 100) {

        s = textureLod(iChannel1, fragCoord/1024.0, 0.0).r;
    }

    fragColor = vec4(s);
}

// from https://www.shadertoy.com/view/fstfDS
float gielisFormula(vec2 teta, vec3 n, vec3 abm){
    float b1 = pow(abs(cos((abm.z*teta.x)/4.0) / abm.x), n.y);
    float b2 = pow(abs(sin((abm.z*teta.y)/4.0) / abm.y), n.z);
    return pow(b1+b2, -1.0/n.x);
}
