float noise(float c, float origo){
    c = sin(origo/0.005);
    return c;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;// перелал координаты uv
    vec2 uv2 = fragCoord.xy / iResolution.xy; // перелал координаты uv2
    float scroll = step(0.0 + (sin(iTime) + 1.0) * .45, uv.y) - step(0.05+ (sin(iTime) + 1.0) * .45, uv.y); //Полоса скролла
    //uv2.y = uv.y*(iResolution.x/iResolution.y); // fix the image ratio
    float box = 0.05; // ширина скролла
    float dist = length(scroll/uv); // расчет скрола к длине 
    if (dist > box){ // если пиксель вне скролла, наложить шум
    uv2.y = noise(0.1, uv.y);
    }
    fragColor = vec4(texture(iChannel0,uv2)) ;

}
