void drawDisc(vec2 uv, vec2 centre, float rad, vec3 col, inout vec3 pixel)
{
    // Using shadertoy shorthand
    if(length(uv - centre) < rad)
    {
        pixel = col;
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord.xy -0.5 * iResolution.xy;
    uv = 2.0 * uv.xy / iResolution.y;

    vec3 circ1Col = vec3(0.28, 0.67, 0.97);
    vec3 circ2Col = vec3(0.68, 0.37, 0.47);
    vec3 circ3Col = vec3(0.58, 0.77, 0.17);
    vec3 circ4Col = vec3(0.94, 0.75, 0.85);
    vec3 finalPixCol = vec3(0.0);
    float alpha = 0.0;
    float circ2Rad = 0.29;
    float circ3Rad = 0.55;
    float circ4Rad = 0.39;

    drawDisc(uv, vec2(0.14, 0.97), 0.78, circ1Col, finalPixCol);
    drawDisc(uv, vec2(-0.44, 0.57), 0.28, circ2Col, finalPixCol);
    drawDisc(uv, vec2(0.54, 0.47), 0.38, circ3Col, finalPixCol);
    drawDisc(uv, vec2(-0.85, 0.17), 0.44, circ4Col, finalPixCol);


    // Output to screen
    fragColor = vec4(finalPixCol, alpha);
}
