void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 col = vec3(0,0,0);
    col+=vec3(texture(iChannel0, uv)) * 0.2;

    float s = 64.0;

    float fade = sin(uv.y*uv.y+iTime*0.1)*0.3;
    float wave1 = sin(iTime*0.0001);
    float wave2 = cos(iTime*0.002);
    float offset = cos(uv.x+iTime*0.001);


    for(int i = 0; i< 12;i++) {
    float localTick = float(i) * 0.33;
      col += fade*cos(iTime+localTick+uv.yyx*vec3((-localTick*s*wave1),localTick+s*wave2,localTick*s*offset));
    }

    fragColor = vec4(vec3(col.x*col.y*col.z),1.0);
}
