void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspectRatio = iResolution.y / iResolution.x;
    vec2 uv = fragCoord / iResolution.xy;
    uv.y *= aspectRatio;

    // Time varying pixel color
    vec3 bgColor = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 shapeColor = vec3(0);

    float radius = 0.1 + 0.0999 * cos(iTime);
    vec2 center = vec2(0.5, 0.5 * aspectRatio);
    float dist = distance(uv, center);

    float isOutOfCircle = smoothstep(radius, radius + 0.05, dist);
    vec3 col = mix(shapeColor, bgColor, isOutOfCircle);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
