#define U (10.)
#define Y (2.)
#define dd(a) (floor(clamp((a),-1.+Y*(1./U),1.-Y*(1./U))*U)*(1./U))
#define r(a) (mat2(cos(a),sin(a),-sin(a),cos(a)))
void mainImage(out vec4 e, in vec2 v) {
    e=vec4(0.);
    vec3 d=vec3((v.xy-iResolution.xy*.5)/iResolution.y,.3),p;
    d.yz*=r(iTime*.2); d.xz*=r(iTime*.2);
    for(float o,i=0.;i<53.;i++){
        o=1.2-length(dd(cos(p.xy))+dd(sin(p.yz-vec2(-1.,iTime*5.))));
        p+=(o-.01)*d;
        e.xyz+=vec3(.3,.3,.3)*(.0009/clamp(abs(o),.01,9.));
    }
}
