float grid(vec2 uv)
{
    #define VANTAGE vec2(0, -0.21)

    uv = uv - VANTAGE;

    float m = max(sign(-uv.y), 0.0);

    uv /= 1.0;

    float d = -1.0 / uv.y;
    vec2 pv = vec2(uv.x * d, d);

    pv *= 1.4545;
    pv.y += iTime;

    const float N = 32.0;

    vec2 w = fwidth(pv) + 0.001;
    vec2 a = pv + 0.5 * w;
    vec2 b = pv - 0.5 * w;
    vec2 i = (floor(a) + min(fract(a) * N, 1.0) - floor(b) - min(fract(b) * N, 1.0)) / (N * w);

    return (i.x + i.y - i.x * i.y) * m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uvo = (fragCoord.xy * 2. / iResolution.xy) - vec2(1);
	vec2 uv = uvo * vec2(iResolution.x/iResolution.y, 1);

    float g = grid(uv);

    fragColor.rgb += g;
    fragColor.a = 1.0;
}
