const float TWO_PI = 6.28318530718,
        distThresh = 0.4,
        baseRadius = 0.2,
      brightAdjust = 5.,
  numControlPoints = 16.,
             speed = 1.;

#define SC(a) vec2(sin(a),cos(a))

void mainImage( out vec4 O,  vec2 U )
{
    U = (2. * U - iResolution.xy) / iResolution.y;

    float time = iTime * speed,
        radius = baseRadius * ( 1. + .7 * sin(time) ),
          dist = 0., d;

    for (float i=0.; i < 1.; i+=1./numControlPoints) {
        d = distance (U, 2.*radius * SC(i*TWO_PI) );
        if (d < distThresh) dist += d;
    }
    //vec3 r =0.5 + 0.5*cos(iTime+vec3(0,2,4))

    O = vec4(cos(iTime+vec3(0,2,4))*dist/numControlPoints * brightAdjust,0);


   // O = vec4(dist/numControlPoints * brightAdjust);
}
