vec3 Lines (vec2 Direction){

    vec2 uv = (Direction.xy-0.5*iResolution.xy)/iResolution.y;

    float Tl = iTime*0.23;

    uv.y -= .125 * sin( 65. * uv.x + Tl );
    uv.y += .125 * sin( 66. * uv.x - Tl );

    float b = round(fract(abs(uv.y + 69.)
        *1.25 //how many lines
    ));

    return vec3(b);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    fragColor = vec4(Lines(fragCoord),1.0);
}
