void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord.xy / iResolution.xy;
float y =
0.3*sin((uv.y + iTime) * 3.0) * 0.038;
float x =
0.8*sin((uv.y + iTime) * 3.0) * 0.01+
0.1*sin((uv.x + iTime) * 8.0) * 0.05;
0.2*sin((uv.x + iTime) * 8.0) * 0.05;
fragColor = texture(iChannel0, 0.79*(uv + vec2(y+0.11, x+0.11)));
}
