// Copied from https://www.shadertoy.com/view/ltcBzj

float distance_to_line (vec2 p, vec2 p1, vec2 p2) {
    vec2 p_p1 = p - p1;
    vec2 p2_p1 = p2 - p1;
    float h = clamp (dot (p_p1, p2_p1) / dot (p2_p1, p2_p1), 0.0, 1.0);
    float d = length (p_p1 - p2_p1 * h);
    return d;
}


vec2 circlePtAnimated(float lt) {
    return vec2(sin(lt*3.1416*2.0), cos(lt*3.1416*2.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;


    float curTimeSeconds = iDate.w;
    vec4 millisSecsMinsHours = vec4(
        mod(curTimeSeconds,                      1.0), 
        mod(floor(curTimeSeconds)  /   60.0,     1.0), 
        mod(floor(curTimeSeconds / 60.0) / 60.0, 1.0), 
        mod((curTimeSeconds / 3600.0) / 12.0,    1.0)
        );
    vec4 lineLen = vec4(0.1, 0.8, 0.75, 0.6) * 0.5;

    float lineThickness = 1.0;

    vec2 ptCenter = iResolution.xy/2.0;
    vec3 color = vec3(0.0);
    vec3 dbgCol = vec3(0.0);
    for (int i = 0; i < 4; i++) {
        if (millisSecsMinsHours[1] > 1.0) {
            dbgCol.r = 1.0;
        }

        vec2 ptEnd = ptCenter + lineLen[i] * circlePtAnimated(millisSecsMinsHours[i]) * iResolution.yy;


        // rgb-subpixel antialiasing
        float colCr = lineThickness - distance_to_line (fragCoord - vec2 (1./3., 0.), ptCenter, ptEnd);
        float colCg = lineThickness - distance_to_line (fragCoord,                    ptCenter, ptEnd);
        float colCb = lineThickness - distance_to_line (fragCoord + vec2 (1./3., 0.), ptCenter, ptEnd);

        color += max(vec3(0.0), vec3(colCr, colCg, colCb));

        lineThickness *= 2.;
    }
    float distCt = length((ptCenter.xy - fragCoord.xy) / iResolution.yy);
    float fadeLen = lineThickness / iResolution.y;
#if 0
    float circle = smoothstep(0.95-fadeLen*0.5, 0.95, distCt * 2.) * smoothstep(0.95+fadeLen*0.5, 0.95, distCt * 2.);
    vec4 col = vec4((color+vec3(circle))*vec3(0.9, 0.8, 0.9)*0.1, 1.0);
#else
    vec4 col = vec4(color, 1.0);
#endif
    fragColor = pow(col,vec4(1./2.2));
}
