#define pi 3.14159

void mainImage(out vec4 fragColor,in vec2 fragCoord){
  vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
  vec3 col = vec3(0.0);

  uv += vec2(sin(iTime/4.0),cos(iTime/2.0))/4.0;

  float phi = atan(uv.y,uv.x)/pi;
  float rho = 0.25/length(uv);

  vec2 pos = vec2(phi,rho);
  pos.x += sin(iTime/3.5);
  pos.y += iTime/2.0;

  if(uv.y>=sin(iTime+1.0)/1.64){
    if(uv.x>=sin(iTime+2.0)/1.64){
      col += textureLod(iChannel0,pos,0.0).rgb;
      col *= 0.3/rho;
      col += min(col,1.0);
    }else{
      col += round(sin(pos.x*50.0))+round(sin(pos.y*50.0));
      col *= 0.3/rho;
    }
  }else{
    if(uv.x>=sin(iTime+3.0)/1.64){
      col += mod(floor(pos.x*16.0)+floor(pos.y*16.0),2.0);
      col += rho*0.25;
      col *= rho;
    }else{
      col += textureLod(iChannel1,pos,0.0).rgb;
      col *= rho;
      col += min(col,1.0);
    }
  }

  fragColor = vec4(col,1.0);
}
