void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
float a=.05;
mat2 m = mat2(cos(a),-sin(a),sin(a),cos(a));
vec2 uv = ((fragCoord.xy-iResolution.xy/2.)*m+iResolution.xy/2.)/iResolution.xy;
vec3 color = texture(iChannel0, uv).rgb - 0.5 / 256.0;
if(length((fragCoord.xy-iResolution.xy/2.+vec2(sin(float(iFrame)*0.15)*iResolution.x*0.25,0.) ))<8.) color=vec3(1.0,0.0,0.0);
fragColor = vec4(color, 1);
}
