const float PI = 3.14159;

float Map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

float Smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
    return mix(a, b, h) - k*h*(1.0-h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv.x +=1.0;

    float sine = sin(iTime*1.3);
    float sineMap1 = Map(sine, -1.0, 1.0, 1.0, 5.0); 
    float sineMap2 = Map(sine, -1.0, 1.0, 0.2, 0.1);

    float dist = sqrt((uv.x*uv.x) + (uv.y*uv.y));
    float arctan = Map(atan(uv.y, uv.x), -PI, PI, 0.0, 1.0);

    float smoothmin = Smin(dist, arctan, sineMap1);

    float modulo = mod(smoothmin, sineMap2);

    float norm = Map(modulo, 0.0, sineMap2, 0.0, 1.0);

    float thresh = norm > 0.5f ? 0.0 : 1.0;

    //fragColor = vec4(vec3(thresh), 1.0);
    fragColor = vec4(vec3(norm*1.3, norm-0.3, norm+0.5), 1.0);
}
