#define K1 0.4
#define K2 0.5
#define C 30.0
#define D 80.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 slit1 = vec2(0.0, iResolution.y/2.0 + D/2.0);
    vec2 slit2 = vec2(0.0, iResolution.y/2.0 - D/2.0);

    float phase1 = K1*(distance(fragCoord, slit1) - C*iTime);
    float phase2 = K1*(distance(fragCoord, slit2) - C*iTime);
    float phase3 = K2*(distance(fragCoord, slit1) - C*iTime);
    float phase4 = K2*(distance(fragCoord, slit2) - C*iTime);

    float value = 0.5*(1.0 + 0.25*(sin(phase1) + sin(phase2) + sin(phase3) + sin(phase4)));

    // Output to screen
    fragColor = vec4(vec3(value), 1.0);
}
