//噪声纹理采样
float noise(vec2 uv){
    return texture(iChannel0, uv).r;//采样uv坐标随时间周期改变
}

float stars(vec2 uv){
    return noise(uv);
}
//判断具体位置
vec2 polar(vec2 uvc){
    float r = sqrt(dot(uvc, uvc));//半径
    float th = atan(uvc.y, uvc.x);//弧度
    return vec2(r, th);   
}

void mainImage( out vec4 O, in vec2 U )
{
   vec2 R = iResolution.xy;
    U -= .5*R;
    O = texture(iChannel0, vec2(length(U/R)+iTime, atan(U.y,U.x) ) ).xxxx;
