#define THICCNEss 0.03

vec2 scr2uv(vec2 scr)
{
    return ((scr - iResolution.xy/2.)/iResolution.y) * 5.;
}

float circle(vec2 uv, float r, vec2 pos)
{
    return (length(uv - pos) - r) * 1. / length(uv - pos);
}

float sceneDist(vec2 uv)
{
    return circle(uv, 1., scr2uv(iMouse.xy)) +
        circle(uv, 0.8, vec2(1)) +
        circle(uv, 2.5, vec2(-1.5)) +
        circle(uv, 0.7, vec2(sin(iTime/1.5) * 2., 1.)) +
        circle(uv, 1., vec2(1., sin(iTime/2.) * 2.));
}


vec3 color(vec2 uv)
{
    float d = sceneDist(uv);
    return vec3( 1. - (abs(d) - THICCNEss ) /min(1.,fwidth(d)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = scr2uv(fragCoord);


    vec3 col = color(uv);


    fragColor = vec4(col,1.0);
}
