#define PI 3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;


	vec2 mouse = iMouse.xy / iResolution.xy;
	if (mouse == vec2(0)) mouse = vec2(0.25);


	float size = 0.45;
	//size *= iResolution.x / 1000.0;

	float angle = atan(uv.y, uv.x) / PI;
    float dist = length(uv) / size;
	dist = pow(dist, 0.1 + mouse.x * 5.0);


	vec3 bg = vec3(0.4);
    vec3 color =  sin(angle * vec3(3,2,1) * cos(iTime*.5) * 5. + dist*10. ) * .5 + .5;

	// attempt to fix harsh line between colors... could be better
	if ((angle - 1.0) < 0.01 || (angle + 1.0) < 0.01) {
		float dTop = abs(angle - 1.0) < 0.01 ? 0.01 - abs(angle - 1.0) : 0.0;
		float dBottom = abs(angle + 1.0) < 0.01 ? 0.01 - abs(angle + 1.0) : 0.0;
		float d = max(dTop, dBottom);
		color = mix(color, bg, d * 105.0);
	}


	float alpha = 1.0 - dist;
	color = mix(color, bg, smoothstep(0.05, 0.0, alpha));
	color = mix(color, color * color * 4.0, smoothstep(0.05, 0.0, alpha));

	fragColor = vec4(color, 1.0);
}
