float MODULE_SIZE = 24.0;
vec4 bg = vec4(0.8, 0.8, 0.8, 1.0);
vec4 fg = vec4(0.4, 0.4, 0.4, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float offset = iMouse.x;
    float scale = 4.0 * (iMouse.y + 1.0) / iResolution.y;
    float logScale = max(floor(-log2(scale)), 0.0);

    float moduleSize = MODULE_SIZE * scale * exp2(logScale);

    float pointSize = clamp(scale, 1.0, 4.0);
    float x = mod(fragCoord.x - offset, moduleSize);
    float y = mod(fragCoord.y, moduleSize);

    x = min(x, moduleSize - x);
    y = min(y, moduleSize - y);

    float d = sqrt(x * x + y * y);
    float blend = smoothstep(pointSize - 0.5, pointSize + 0.5, d); 

    // Output to screen
    fragColor = mix(fg, bg, blend);
}
