// Fork of "Procedual Weave Pattern (func)" by shiyuugo. https://shadertoy.com/view/sldyWB
// Fork of "Procedual Weave Pattern" by shiyuugo. https://shadertoy.com/view/stdyWB

// weave()
// UV    : canvasUV (px)
// width : thread width(px)
// yarn  : thread grouping number
// over  : warp over number
// under : warp under number
// sett  : thread group showing pattern number 
//       : width*yarn*sett = tile pattern size (px) 
vec2 weave(vec2 UV, int width, int yarn, int over, int under, int sett){

	float xp = floor(UV.x/float(yarn)/float(width));
	float yp = floor(UV.y/float(yarn)/float(width));
    bool warp = mod(abs(xp-yp), float(over+under))>=float(under);

	float m = fract( (warp?UV.x:UV.y) / float(width));
          m = m>0.5 ? 1.-m : m;
    float s = fract( (warp?xp:yp) / float(sett));
          s = s>0.5 ? 1.-s : s; // symmetry
    return vec2(m, s);
}

vec3 calcWeaveNormal(vec2 UV, int width, int yarn, int over, int under, int sett){
    float strength = 3.;
    float w = weave(UV,width, yarn,over,under,sett).x;
    float dx = w - weave(UV+vec2(1,0),width, yarn,over,under,sett).x;
    float dy = w - weave(UV+vec2(0,1),width, yarn,over,under,sett).x;
    return normalize(vec3(vec2(dx,dy)*strength,1.0));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 1.;
    float t = iTime*.5;
    float fbmx = fbm(fragCoord.xy/iResolution.xy, 2, t);    
    float fbmy = fbm(fragCoord.yx/iResolution.yx, 2, t);
    vec2 noise = 0. * vec2(fbmx,fbmy) ;

    int width = 4;
    int yarn = 1;
    int over= 2;
    int under = 1;
    int sett = 40;

    vec2 w = weave(fragCoord.xy * zoom + noise, width,yarn,over,under,sett);
    //fragColor = vec4(w.x); return;

    vec3 normal = calcWeaveNormal(fragCoord.xy * zoom + noise, width,yarn,over,under,sett);
    //fragColor = vec4(normal, 1.); return;

    //--- phong
    vec2 st = (2.0*fragCoord-iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 mp = (2.0*iMouse.xy-iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 lightPos = vec3(mp, 5.0);
    vec3 lightDir = normalize(vec3(lightPos - vec3(st, 0.0)));
    vec3 ambientColor = vec3(0.0);
    vec4 diffuse = vec4(0.0); // R,G,B,diffuse
         diffuse.a = max(0.0, dot(normal, lightDir));
    vec3 vReflection = normalize(reflect(-lightDir, normal));
    float specular = 0.08 * pow(max(0.0, dot(normal, vReflection)), 100.0);

	vec4 col = vec4(0.9,0.1,0.1,1.0);
         col = w.y <.10 ? vec4(0.0,.5,0.0,1.0) :
               w.y <.15 ? vec4(0.0,0.5,0.9,1.0) :
               w.y <.2 ? vec4(1.0,1.0,0.0,1.0) : col;
    float dirt = mix(0.75,1.0, fbm(fragCoord.xy*.15, 3, 0.));
    //fragColor = vec4(dirt); return;
    col *= dirt; 

    diffuse.rgb = col.rgb;
    diffuse.rgb *= float(w.x>.2);
    //specular *=  float(w.x>.4);

    fragColor.rgb = min(vec3(1.0), ambientColor + diffuse.rgb*diffuse.a + specular) ;


}

