// variant of https://shadertoy.com/view/ftdcWX

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;
    float i = floor( textureLod(iChannel0, U/vec2(6,1) , 2. ).a * 3.99  ); // mask source
    if ( mod(iTime/4.,2.) > 1. ) i = 3.-i;               // color order 
    O = sqrt( vec4(i/3.,1.-i/3.,0,0) );                  // color ( sqrt for sRGB conversion )
 // O = vec4(1);                                         // B&W variant
 // O = vec4(sqrt(1.+i)/2.);                             // grey variant
    if ( mod(iTime/8.,2.) > 1. ) i = 3.-i;               // motion order
    O *= texture(iChannel1, U + .1*iTime*vec2(i,0) ).r;  // morion
}
