// Distance to an arrow

// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and iquilezles.org/articles/distfunctions2d

float sdCircle(in vec2 uv, vec2 original_fix, in float tiling, float size_inner, float size_outer) 
{
    uv = uv*tiling;
    return smoothstep(length(fract(uv)-original_fix),size_inner,size_outer);
}
// The arrow goes from a to b. It's thickness is w1. The arrow
// head's thickness is w2.
float sdArrow( in vec2 p, vec2 a, vec2 b, float w1, float w2 )
{
    // constant setup
    const float k = 3.0;   // arrow head ratio
	vec2  ba = b - a;
    float l2 = dot(ba,ba);
    float l = sqrt(l2);

    // pixel setup
    p = p-a;
    p = mat2(ba.x,-ba.y,ba.y,ba.x)*p/l;
    p.y = abs(p.y);
    vec2 pz = p-vec2(l-w2*k,w2);

    // === distance (four segments) === 

    vec2 q = p;
    q.x -= clamp( q.x, 0.0, l-w2*k );
    q.y -= w1;
    float di = dot(q,q);
    //----
    q = pz;
    q.y -= clamp( q.y, w1-w2, 0.0 );
    di = min( di, dot(q,q) );
    //----
    if( p.x<w1 ) // conditional is optional
    {
    q = p;
    q.y -= clamp( q.y, 0.0, w1 );
    di = min( di, dot(q,q) );
    }
    //----
    if( pz.x>0.0 ) // conditional is optional
    {
    q = pz;
    q -= vec2(k,-1.0)*clamp( (q.x*k-q.y)/(k*k+1.0), 0.0, w2 );
    di = min( di, dot(q,q) );
    }

    // === sign === 

    float si = 1.0;
    float z = l - p.x;
    if( min(p.x,z)>0.0 ) //if( p.x>0.0 && z>0.0 )
    {
      float h = (pz.x<0.0) ? w1 : z/k;
      if( p.y<h ) si = -1.0;
    }
    return si*sqrt(di);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    vec2 center = vec2(0.5,0.5);
    float size_inner = 0.4;
    float size_outer = 0.0;
    float circle = sdCircle(p,center,10.0,size_inner,size_outer);

    p = p - vec2(0.0,ceil(fract(iTime/4.0)*11.0)/3.5-0.5);
    vec2 a1 = vec2(0.0,-2.0);
    vec2 b1 = vec2(0.0,-1.0);
    // vec2 a2 = vec2(1.0,-2.0);
    // vec2 b2 = vec2(1.0,-1.0);
    // vec2 a3 = vec2(-1.0,-2.0);
    // vec2 b3 = vec2(-1.0,-1.0);
    // // distance
    float w1 = 0.09;
    float w2 = 0.2;
    float arrow = sdArrow(p, a1, b1, w1, w2);
    float arrowBlur = pow(arrow,0.2)*0.80;
    // float e = sdArrow(p, a2, b2, w1, w2);
    // float f = sdArrow(p, a3, b3, w1, w2);
    // coloring
    // vec3 col = vec3(1.0) - sign(d) * sign(e) * sign(f);
    vec3 col = vec3(1.0) - max(arrowBlur,circle);

    //output
	fragColor = vec4(col, 1.0);
}
