// more readable version below

// ---  -13 chars by coyote

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = u+u - R,
         I = ceil(U*=8./R.y),             // cell Id
         N = round(U);                    // node Id

    u -= u - atan( 20.* sin( .5*iTime + sin(dot(N,7.+R)) *1e3 ));

    O = smoothstep(24./R.y, 0., vec4( abs(U-N)-.01, length(U-N)-.2*abs(u) ));

    O += mix( mix( mod(I+I.y,2.),         // checkered background
                   step(0.,u), O.zw).x,   // dots at nodes
                   .6, max(O.x,O.y) ) -O; // grey lines
}






/** //--- 286 chars

#define S(v)    smoothstep(24./R.y, 0., v)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 8.* ( u+u - R ) / R.y,
         I = ceil(U),                                         // cell Id
         N = round(U),                                        // node Id
      // F = U - N,                                           // node local coords
         L = S( abs(U-N) -.01 );                              // lines
                               // ease-flip every Pi seconds with random phase
    float r = atan( 20.* sin( .5*iTime + sin(dot(N,7.+R)) *1e3 ));
    O += mix( mix( mod(I.x+I.y,2.),                           // checkered background
                   step(0.,r), S( length(U-N) - .2*abs(r) )), // dots at nodes
                   .6, max(L.x,L.y) ) -O;                     // grey lines
}





/**  // --- 338 chars

//#define H(p)  fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)
#define H(p)    fract(sin(dot(p, 7.+R)) * 4e4 )
#define S(v)    smoothstep(24./R.y, 0., v)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 8.* ( 2.*u - R ) / R.y,
         I = ceil(U);                                     // cell Id

    float r = atan(20.*sin(.5*iTime + 6.28*H(round(U)))); // ease-flip every Pi seconds with random phase
    U = fract(U+.5) -.5;                                  // node local coords
    O = mix( vec4( mod(I.x+I.y,2.) ),                     // checkered background
             vec4(r>0.), S( length(U) - .2*abs(r) ));     // dots at nodes
    U = S( abs(U) -.01 );
    O = mix(O, vec4(.6), max(U.x,U.y) );                  // grey lines
}

