bool Sample(uvec2 UV, vec2 subUV, float AA) {
    vec2 uv = (vec2(UV) + 0.5) - subUV + vec2(1.0 / AA);

    vec2 circle = vec2(180.0 + sin(iTime) * 2.5, 150.0 + cos(iTime) * 2.5);
    float radius = 100.0;

    float dist = distance(circle, uv);

    return dist < radius && dist > radius - 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;

    vec3 col = vec3(0.0);

    float AA = 16.0;
    vec3 aCol = vec3(0.0);
    for(float subX = 0.0; subX <= 1.0; subX += 1.0 / AA)
        for(float subY = 0.0; subY <= 1.0; subY += 1.0 / AA) {
            if(Sample(uvec2(fragCoord), vec2(subX,subY), AA))
                aCol += vec3(1.0);
        }

    col = aCol / (AA * AA);



    fragColor = vec4(col, 1.0);
}
