void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q=uv-vec2(0.5,0.5);
    q.x*=iResolution.x/iResolution.y;

    float inputVal=fract(iTime);
    //inputVal=inputVal*2.0-1.0;
    inputVal=2.0*sin(iTime);
    float insertVal=(inputVal+2.0)*0.625-0.5;

    float curveVal=1.0-abs(2.0*sin(2.0*atan(q.y,q.x)+inputVal));
    float r=0.3+0.1*insertVal*smoothstep(0.3,1.3,curveVal);
    r*=abs(inputVal);
    float Result =smoothstep(r+0.01,r,length(q));

    // Output to screen
    fragColor = vec4(0,0.7391,1.0,1.0)*Result;
}
