    float circle(vec2 a, vec2 point, float r, float blur)
    {
        float b=length(a-point);
         float c=smoothstep(r,r-blur,b);
        return c;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 a = fragCoord.xy/iResolution.xy;
     a-=.5;
     a.x*=iResolution.x/iResolution.y;
    float c=circle(a, vec2(.0,-.1), .3, .01);
     c+=circle(a, vec2(-.3, .1), .1, .01);
     c+=circle(a, vec2(.3, .1), .1, .01);
     c+=circle(a, vec2(.2, -.41), .1, .01);
     c+=circle(a, vec2(-.2, -.41), .1, .01);
     c+=circle(a, vec2(.0, .32), .2, .01);
     c-=circle(a, vec2(-.08, .4), .05, .01);
     c-=circle(a, vec2(.08, .4), .05, .01);
    vec3 color=vec3(0.);
     color=vec3(0., 0., 1.)*c;
    fragColor = vec4(color, 1);
}
