//<200 chars playlist: https://www.shadertoy.com/playlist/N3lGDN

void mainImage(out vec4 O,vec2 I)
{
    //Make sure O = vec4(0).
    O -= O;
    //Iterate through layers.
    for(float i=9.,r; i<2e2; i+=9.)
    {
        //Compute radius with pseudo randomness.
        r = i+tan(i);
        //Layer color and intensity.
        O += (cos(i+vec4(1,2,3,0))+1.)*i/6e2*
        //Circle rings.
        max(0.,2.-abs(length(mod(I+=vec2(r,9)+iTime/.2,r+r)-r)-.8*r));
    }
}
