float HorOval(vec2 uv, vec2 position, float r, float blur) 
    {
        uv.y *= (iResolution.x / iResolution.y);
        uv.x *= (iResolution.x / iResolution.y);
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
            return c;
    }
float VerOval(vec2 uv, vec2 position, float r, float blur) 
    {
        uv.y *= (iResolution.x / iResolution.y);
        uv.x *= (iResolution.x / iResolution.y * 5.5);
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
            return c;
     }
float Circle(vec2 uv, vec2 position, float r, float blur) 
     {
        uv.x *= (iResolution.x / iResolution.y);
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
             return c;
     }

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = fragCoord.xy / iResolution.xy;    
          uv -= .5; 

     float c = Circle(uv, vec2(.0, -.05), .2, .005);

            // Ноги
            c += HorOval(uv, vec2(.1, -.46), .078, .005);
            c += HorOval(uv, vec2(-.1, -.46), .078, .005);


            // Уши
            c += VerOval(uv, vec2(-.4, .4), .35, .005);
            c += VerOval(uv, vec2(.4, .4), .35, .005);

            // глаза
            c -= Circle(uv, vec2(-.07, .0), .04, .009);
            c -= Circle(uv, vec2(.07, .0), .04, .009);
            c += Circle(uv, vec2(-.07, .0), .02, .009);
            c += Circle(uv, vec2(.07, .0), .02, .009);

            // Руки
            c += Circle(uv, vec2(.21, -.1), .04, .005);
            c += Circle(uv, vec2(-.21, -.1), .04, .005);

            //Poт

            c -= Circle(uv, vec2(.0, -.08), .11, .009);
            c += Circle(uv, vec2(.0, -.07), .11, .009);


    fragColor = vec4(vec3(c)*vec3(0.,0.,1.), 70.);
}
