#define M_PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;


    //scale of stripes                                 


     float a = mod((atan(uv.y, uv.x) + M_PI + (M_PI)) * 5.0 / M_PI, 2.0) - 0.9;
                                                                     //# of stripes.   //scale                                    //controls # of stripes


     //float m = d < 0.0 ? 0.0 : 1.0;
     float m = clamp(a*100.0, 0.0, 1.0);

     //vec3 col = mix(vec3(1.0, 0.2, 0.4), vec3(1.0, 1.0, 0.4), m);
     vec3 col = mix(vec3(1.0, 1.0, 1.0), vec3(0.2, 0.2, 0.2), m);


	fragColor = vec4(col,1.0);
}
