//check FabriceNeyret2 comment better and shorter code
#define s smoothstep
vec2 u;float x,y,z,t;
#define h(r,a,b) (1.-s(z,.01,abs(x*x+y*y-r*r)-.17*r))*s(z,.01,abs(atan(y,x)-a)/3.14-b)/.01
#define k(a,b,p) ((1.-s(z,.01,abs(x+p*y)-a))*(1.-s(z,.01,abs(y-p*x)-b)))/.01
void mainImage( out vec4 o, in vec2 i )
{
    u=i/iResolution.y*4.-2.;
    t=iTime/2.;t=sin(t*2.)+cos(t/.17)+sin(t)*2.;t=sin(t*2.)+3.;u+=sin(u*t+t*5.)/5.;//wobble
    x=u.x+.5;y=u.y;
    //G
    o.z+=h(.7,.39,.12);x-=.4;o.z+=k(.38,.09,z);
    //oo
    x-=.9;y+=.3; o.x+=h(.4,z,-x);x--; o.xy+=h(.4,z,-x);x--; //fix by @iq, h(,9.,z) also work great by @FabriceNeyret2
    //g
    o.z+=h(.4,z,z);
    y+=.5;x=-x;o.z+=h(.4,1.57,.66);x=-x-.39;y-=.5;
    o.z+=k(.09,.5,z);
    //L
    x-=.3;y-=.2;o.y+=k(.1,.7,z);
    //e
    x-=.7;y+=.2;o.x+=h(.4,z,.16);y-=.08;o.x+=k(.14,.8,-1.8);
    o*=.03;
}
