// grid step
const vec2 space = vec2(20.0,30.0);

// offset
const vec2 offset = vec2(0.0,10.0);

// line width
const float lineWidth = 2.0;

// color
const vec4 LineColor = vec4(0.1, 0.7, 0.1, 1.0);
const vec4 NULL = vec4(0.0, 0.0, 0.0, 0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float half_lw = lineWidth * 0.5;
    vec2        p = fragCoord - offset,
              hfh = vec2(half_lw * 0.5),
              fp1 = mod(p - hfh, space),
              fp2 = mod(p + hfh, space);           

    fragColor = fp1.x <= half_lw ||
                fp1.y <= half_lw ||
                fp2.x <= half_lw ||
                fp2.y <= half_lw ? LineColor : NULL;    
}
