int draw(float x0, float y0) {

    float x = 0.0;
    float y = 0.0;

    int iteration = 0;
    int max_iteration = 1000;

    while ((x*x - y*y) <= 4.0 && iteration < max_iteration)
    { 
        float xTemp = x*x - y*y + x0;
        y = 2.0*x*y + y0;

        x = xTemp;

        iteration++;
    }

    return iteration;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x0 = 2.0*fragCoord.x/iResolution.x-2.0;
    float y0 = 2.0*fragCoord.y/iResolution.y-1.0;

    // Output to screen
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    int iteration = draw(x0, y0);
    fragColor += vec4(float(iteration-50)/25.0, float(iteration)/25.0, float(iteration)/25.0, 1.0);
}
