const int cols = 40;
const int rows = 25;

const int underscore = 95;
const int space = 32;

int[13] text = int[13](72,101,108,108,111,44,space,87,111,114,108,100,33);

vec2 getCharPos( in int char ) 
{
    float x = mod(float(char), 16.0);
    float y = (float(char) - x) / 16.0;
    vec2 uv = vec2(x, y) / 16.0;
    uv.y = 1.0 - (uv.y + 1.0 / 16.0);
    return uv;
}

int getCurrentChar(in vec2 uv)
{
    int col = int(floor(uv.x * float(cols)));
    int row = int(floor((1.0 - uv.y) * float(rows)));
    int pos = col + (row * cols);

    int cursor = mod(iTime, 1.0) < 0.5 ? underscore : space;
    int currentChar = pos > text.length() - 1 ? space : text[pos];
    currentChar = pos == text.length() ? cursor : currentChar;
    return currentChar;
}

vec2 convertGridToTexCoord(in vec2 uv) 
{
    uv.x = mod(uv.x, 1.0 / float(cols)) / 16.0 * float(cols);
    uv.y = mod(uv.y, 1.0 / float(rows)) / 16.0 * float(rows);

    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    int currentChar = getCurrentChar(uv);

    vec2 charPos = getCharPos(currentChar);
    vec2 offset = convertGridToTexCoord(uv);
    vec2 charUv = vec2(charPos.x + offset.x, charPos.y + offset.y);

    float r = texture(iChannel0, charUv).r;
    fragColor = vec4(0.0, r, 0.0, 0.0);

}
