const float PI = 3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Hint, it's a few lines of code, and makes use of a tan function :)
    vec2 warpedUv = uv;
    float input_angle = iTime * 0.05;
    if(tan(input_angle) < 0.0){
    input_angle += PI/4.0;
    }
    //input_angle = 0.1;
    float bandPosOffset = 0.4+ tan(input_angle);

    //if(bandPosOffset > 0.9){
    if(warpedUv.y > bandPosOffset && warpedUv.y < bandPosOffset + 0.1){
        warpedUv.x = (tan((uv.x*1.0+(iTime+(0.4*(warpedUv.y-bandPosOffset)))* 0.9) / (uv.y*1.8)) + uv.y*0.0)*0.2 + uv.x * 0.8;

        //warpedUv.x += iTime;
    }

    vec2 offset1 = vec2(0.01);
    vec2 offset2 = vec2(-0.01);
    vec2 offset3 = vec2(0.0, -0.02);

    float r = texture(iChannel0, warpedUv + offset1).r * 0.8 + texture(iChannel0, uv + offset1).r * 0.2;
    float g = texture(iChannel0, warpedUv + offset2).g * 0.8 + texture(iChannel0, uv + offset1).g * 0.2;
    float b = texture(iChannel0, warpedUv + offset3).b * 0.8 + texture(iChannel0, uv + offset1).b * 0.2;

    vec3 col = vec3(r, g, b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
