float rand(vec2 n)
{
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}


float rand(float n){return fract(sin(n) * 43758.5453123);}

float noise(float p){
	float fl = floor(p);
  float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}

float noise(vec2 n) {
	const vec2 d = vec2(0.0, 1.0);
  vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
	return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

float triangle(float x)
{
    return abs(fract(x) - .5) * 2.;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y = uv.y * 2. - 1.;

    // Time varying pixel color
    vec3 col = vec3(1.);

    float i = uv.x*180.;

    float t = texelFetch( iChannel0, ivec2(i,1), 0 ).x;

    float s = smoothstep(0., 1., triangle(i) + .3);
    //s *= noise((floor(i+.5)));
    s *= smoothstep(0.1, .8, noise(vec2(uv.y*20., floor(i+.5)) + rand(t) * 1200.));
    s *= s;
    //s *= s;

    s *= smoothstep(.3, .4, texelFetch( iChannel0, ivec2(i,0), 0 ).x - abs(uv.y));

    col = vec3(s);

    // Output to screen
    fragColor = vec4(col,1.0);
}
