void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(1.0,0.3,0.4) + 0.1*cos(iTime+uv.xyx+vec3(0,1,1));

    vec4 ns = texture( iChannel0, uv );

    float gltr1 = fract(iTime/121.0);
    float gltr2 = fract(iTime/1922.0);
    float gltr3 = fract(iTime/2363.0);
    float gltr4 = fract(iTime/3435.0);
    float glit1 = smoothstep( gltr1-0.001, gltr1, ns.x ) * smoothstep( gltr1+0.001, gltr1, ns.x );
    float glit2 = smoothstep( gltr2-0.001, gltr2, ns.y ) * smoothstep( gltr2+0.001, gltr2, ns.y );
    float glit3 = smoothstep( gltr3-0.001, gltr3, ns.z ) * smoothstep( gltr3+0.001, gltr3, ns.z );
    float glit4 = smoothstep( gltr4-0.001, gltr4, ns.w ) * smoothstep( gltr4+0.001, gltr4, ns.w );

    col = max( max( max(vec3(glit1),vec3(glit2)), max(vec3(glit1),vec3(glit2)) ), col );

    // Output to screen
    fragColor = vec4(col,1.0);
}
