vec3 background = vec3(248.0 / 255.0, 241.0 / 255.0, 225.0 / 255.0);
vec3 red = vec3(168.0 / 255.0, 34.0 / 255.0, 35.0 / 255.0);
vec3 yellow = vec3(254.0 / 255.0, 195.0 / 255.0, 45.0 / 255.0);
vec3 blue = vec3(0.0, 93.0 / 255.0, 151.0 / 255.0);
vec3 black = vec3(0.0);

float drawRect(vec2 st, vec2 start, vec2 end) {
  return step(st.x, end.x) * step(1. - st.x, 1. - start.x) * step(1. - st.y, 1. - start.y) * step(st.y, end.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 st = vec2(fragCoord.x / iResolution.x, 1. - fragCoord.y / iResolution.y);
  vec3 color = background;

  float redRect = drawRect(st, vec2(0., 0.), vec2(0.21, 0.37));
  float yellowRect = drawRect(st, vec2(0.955, 0.), vec2(1.0, 0.37));
  float blueRect = drawRect(st, vec2(0.73, 0.94), vec2(1.0, 1.0));
  float lineRect = 
    // horizental
  drawRect(st, vec2(0., 0.2), vec2(1., 0.225)) 
  + drawRect(st, vec2(0., 0.37), vec2(1., 0.395)) 
  + drawRect(st, vec2(0.21, 0.915), vec2(1., 0.94))
    // vertical
  + drawRect(st, vec2(0.11, 0.), vec2(0.135, 0.37)) 
  + drawRect(st, vec2(0.21, 0.), vec2(0.235, 1.)) 
  + drawRect(st, vec2(0.705, 0.), vec2(0.73, 1.)) 
  + drawRect(st, vec2(0.93, 0.), vec2(0.955, 1.));

  color = redRect * red +
    yellowRect * yellow +
    blueRect * blue +
    lineRect * black +
    (1. - redRect - yellowRect - blueRect - lineRect) * background;
  fragColor = vec4(color, 1.0);
}
