void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Getting aspect ratio and square-uv.
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= aspect;

    // Creating circle.
    float sqrDist = dot(uv, uv);
    float delta = fwidth(sqrt(sqrDist));
    float alpha = 1.0 - smoothstep(1.0 - delta, 1.0 + delta, sqrDist);

    // Assigning circle color.
    vec3 color = vec3(1.0, 1.0, 1.0);
    color *= 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0,2,4));

    // Setting circle color with alpha.
    fragColor = vec4(color * alpha, alpha);
}
