void mainImage( out vec4 O, vec2 u )
{
    vec2 U = ( 2.* u- iResolution.xy ) / iResolution.y;

    float a = atan(U.y, U.x),
          l = length(U),
          x = 48.* ( l-.3 + sin(iTime)*.06125 ),
          c = abs(cos(x*2.) / x ) * 1.75,
          t = iTime*.75,
          d =   sin(a*1.+t*0.5)
              + sin(a*2.-t*1.2)
              + sin(a*3.+t*1.5)
              + sin(a*2.-t*1.7)
              + sin(a*1.+t*3.8);

    O = vec4(.0,.8,.0,.5) * (.05 + c*d*.3);
}
