void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mat3 imageConvolution = mat3(-1.0, -1.0, -1.0,
                       -1.0,  8.0, -1.0,
                       -1.0,  -1.0, -1.0);

    vec3 col;

    for(int i = -1; i < 2; i++)
    {
        for(int j = -1; j < 2; j++)
        {    
            vec2 coord = fragCoord + vec2(i,j);
            vec2 uv = coord/iResolution.xy;
            col += texture( iChannel0, uv).xyz * imageConvolution[i][j];
        }
    }

    fragColor = vec4(col,1.0);
}
