void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec4 col = texture(iChannel0, uv);
    vec4 bloom = vec4(0,0,0,0);
    float bloomStrength = iMouse.x / iResolution.x;

    // accumulate bloom by sampling low resolution mips
    for (int i = 1; i < 8; i++)
        bloom += textureLod(iChannel0, uv, float(i)) / float(i);

    col += bloom * bloomStrength;

    fragColor = vec4(col.rgb, 1.0);
}
