vec3 col1;
vec3 col2;
float frequency;
float scrollSpeed;

float normalSin (float t)
{
return sin(t) +0.5 *0.5;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    frequency = 100.0;
    uv.x*= frequency;
    col1= vec3(1.0,0.0,0.0);
    col2= vec3(0.0,1.0,0.0);
    scrollSpeed = 10.0;
   vec3 col=mix(col1,col2,sin(scrollSpeed*iTime+uv.x));

    // Output to screen
    fragColor = vec4(col,1.0);
}
