float plot(vec2 st) {    
    return smoothstep(0.02, 0.0, abs(st.y - st.x));
}

float plot(vec2 uv, float y){
  return  smoothstep( y-0.02, y, uv.y) -
          smoothstep( y, y+0.02, uv.y);
}

float rand(float x){
    return fract(sin(x)*100.0);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-0.5;
    uv.y += 0.5;

    //float y = uv.x*uv.x + uv.y*uv.y;
    //float yo = fract(y*100.0*sin(iTime*0.001));
    //float yo = fract(y*20.0);
    //float yoo = fract(y*60.0);
    float y = rand(uv.y*uv.x);

    //float i = floor(uv.x);  // integer
    //float f = fract(uv.x);  // fraction
    //y = rand(i);

    vec3 col = vec3(0.0);

    //float p = plot(uv);
    //float p = plot(uv, yo);
    //float po = plot(uv, yoo);
    float pp = plot(uv,y);

    col = col + pp;

    //output color
    fragColor = vec4(col,1.0);
}
