#define BW 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = step(texture(iChannel1, uv).rgb, texture(iChannel0, uv).rgb);

#if BW
    float bw_col = step(dot(vec3(1.0), texture(iChannel1, uv).rgb),
                        dot(vec3(1.0), texture(iChannel0, uv).rgb));
    col = vec3(bw_col);
#endif

    // Output to screen
    fragColor = vec4(col,1.0);
}
