vec4 drawCircle(float radius, vec2 uv,vec4 color,vec4 tex){   
    vec2 cirCenter = vec2(iResolution.x,iResolution.y)/2.0;



    float x2 = pow((uv.x-cirCenter.x),2.0); 
    float y2 = pow((uv.y-cirCenter.y),2.0);
    float r2 = pow(radius,2.0);

    if((abs(x2)+abs(y2))<abs(r2)){
         return color;
    }else{
         return tex;
    }

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // fragCoord
    vec2 actCoord = fragCoord;

    //texture
   // vec4 tex = texture(iChannel0,uv);

    //color
    vec4 bg = vec4(1.0,1.0,1.0,1.0);



   // vec4 red = vec4(1.0,0.0,0.0,1.0);

    float i = texture(iChannel0,vec2(uv.x-0.5/iResolution.x,1.0)).r;

    vec2 sc = uv;


    //vec4 red = texture(iChannel1,sc);

    vec4 red = vec4(1.0,0.0,0.0,1.0);


    vec2 scor = uv/0.5;

    scor = scor * sin(iTimeDelta)*2.0;

    vec4 tNoise = texture(iChannel2,scor*i*sin(iTime)*cos(iTime));


    vec4 c = drawCircle(abs(i)*400.0,actCoord,red,bg);

    vec4 d = drawCircle((i*200.0),actCoord,red,bg);



    c = c+d+tNoise;

   // bg.r = bg.r+(1.0 * sin(iTime));
  //  bg.g = bg.g+(1.0 * cos(iTime));

    fragColor = bg*c;
}
