void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 3.5;

    float r = sqrt(uv.x*uv.x+uv.y*uv.y)/15.0;
    float size1 = 2.0 * sin(iTime/5.);

    float distor = 20.;

    float a = 60.0 * sin(distor*atan(uv.x+size1*uv.y/r,uv.y-size1*uv.x/r)+iTime);
    a += 500.0 - cos(iTime)*50.;
    a = a*(r/50.0);
    a = 200000.0*sin(a*6.0)*(r/30.0);

    vec3 col = vec3(cos(a/20.0),a/cos(a/200.0),sin(a/200.0));

    fragColor = vec4(col, 1.0 );
}
