void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = vec4(abs(uv.y-1.),abs(uv.y-1.),1.,1.);

    float offset = mod(uv.x - iTime, 1.);

    if ((offset <= 0.45 && offset >= 0.4 && uv.y >= 0.2 && uv.y <= 0.3))
        fragColor = vec4(0.34,0.36,190./255.,1.);
    if ((uv.x <= 1. && uv.x >= 0.0 && uv.y >= 0.0 && uv.y <= 0.2))
        fragColor = vec4(0.9,0.5,19/255,1.);
    if ((uv.x <= 1. && uv.x >= 0.0 && uv.y >= 0.15 && uv.y <= 0.2))
        fragColor = vec4(0.0,0.9,19/255,1.);
}
