vec4 lineH(float center, vec2 uv, vec3 color) 
{
    return vec4(color, 1.0 - clamp(abs(center - uv.x + sin(uv.y * 20.0) * 0.02), 0.0, 1.0));
}
vec4 circle(vec2 center, vec2 uv, vec3 color)
{
    return vec4(color, 1.0 - clamp(length(center - uv) + sin(uv.x + uv.y * 100.0) * 0.02, 0.0, 1.0));
}
vec4 cutUpper(vec2 uv, float from, vec4 color)
{
    return color * smoothstep(clamp(from - uv.y, 0.0, 1.0), 0.01, 0.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 center = vec2(0.5, 0.5);
    vec4 bg = vec4(0.0, 0.0, 0.0, 1.0);

    vec4 circleCol = circle(center + vec2(0.02, 0.0), uv, vec3(1.0, 0.0, 0.0));
    vec4 lineCol = lineH(center.x, uv, vec3(0.0, 1.0, 0.0));

    fragColor = mix(lineCol, bg, smoothstep(lineCol.a, 0.99, 1.0));
    fragColor = mix(circleCol, fragColor, smoothstep(circleCol.a, 0.9, 1.0));
    fragColor = cutUpper(uv, abs(0.5 * cos(iTime * 0.5)), fragColor);
    fragColor = vec4(fragColor.rgb, 1.0);
}
