// v1.0.1

#define PI 3.1415
#define ITERATIONS 25.

float ran21(vec2 uv) {
    return fract(cos(dot(cos(uv.x*uv.y)-32.2,tan(uv.x/uv.y)-23.5)*1322.24)*432122.62);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;
    vec2 uvb = uv;

    vec3 col = vec3(0);


        vec2 gv = fract(uv)-.5;
        vec2 id = floor(uv);


    for(float i=1.;i<=ITERATIONS;i++) {
        float cur = i/ITERATIONS;
        uv = uvb;

        uv *= (ITERATIONS*ITERATIONS/(ITERATIONS+i))-(cur*1.)/(length(uvb/uv));
        uv.y += iTime*0.6;

        gv = fract(uv)-.5;
        id = floor(uv);


        float mul = mod(id.x+id.y,2.)>=.5 ? -1. : ITERATIONS/i;


        float modval = mod(iTime/1000., mul*PI);

        gv.x *= (ran21(id)>(.5)) ? -1. - mod(modval+id.y, mod(id.y, gv.x)) * mul+ abs(cos( -iTime/gv.x - modval/gv.y)* modval) : 
                                    1. + mod(modval+id.y, mod(id.y, gv.x)*.1) * mul-abs(cos( -iTime/gv.x - modval/gv.y)* modval);

        // gv.x *= (ran21(id)>(.5)) ? -1.- abs(sin(iTime+gv.x+modval*PI)*modval) : 1.+ abs(cos(-iTime-gv.x-i*PI)*modval);  

        vec2 ruv = gv-sign(gv.x+gv.y+.001)*.5;
        float tile = smoothstep(.01,-.01,abs(length(ruv)-.5)-.1);
        float rot = atan(ruv.x,ruv.y)/PI;


        float h = smoothstep((ITERATIONS/cur+.1)/gv.x,cur,sin(abs(rot*cur*mul+iTime)*PI)*.25+.75);

        col.b -= (h*tile*cur)/(h/i);
        col.b *= .85-((h/i)/(h*tile*cur));
        col.gb *= .95-((col.r + col.g )/(h*i));


        col = max(col,h*tile*cur);
    }

    fragColor = vec4(col,1.0);
}
