#define T(x) texture(iChannel0, fract((x)/iResolution.xy))

void mainImage(out vec4 c, vec2 u)
{   
    //c=1./u.yyyx;
    c=u.yyyx/1e4;///iTime;
    //for(float t=1.4; t<1e2; t+=t)
    //    c += (c.gbar-c)/3.+T(u-c.wz*t);
    for(float t=.6; t<4e2; t+=t)
    {
        float f1 = 0.8;
        float f2 = f1;
        float f3 = f1;

        //float s1 = pow(sin(iTime*f1+3.14*2.*0.00)*0.5+0.5,0.2);
        //float s2 = pow(sin(iTime*f2+3.14*2.*0.33)*0.5+0.5,0.2);
        //float s3 = pow(sin(iTime*f3+3.14*2.*0.66)*0.5+0.5,0.2);
        float s1 = sin(iTime*f1+3.14*2.*0.00)*0.5+0.5;
        float s2 = sin(iTime*f2+3.14*2.*0.33)*0.5+0.5;
        float s3 = sin(iTime*f3+3.14*2.*0.66)*0.5+0.5;
        s1 = sign(s1)*pow(s1,0.2);
        s2 = sign(s2)*pow(s2,0.2);
        s3 = sign(s3)*pow(s3,0.2);

        vec4 v1 = c.gbar/(sin(iTime*0.1517)*8.)-c*.3+T(u-c.wz*t);
    	vec4 v2 = c.gbar/(cos(iTime*0.1335)*8.)-c*.3+T(u-c.wz*t);
    	vec4 v3 = c.gbar/(tan(iTime*0.1229)*8.)-c*.3+T(u-c.wz*t);

        //c += (c.gbar-c)/3.+T(u-c.wz*t);
        c += v1 * s1;
        c += v2 * s2;
        c += v3 * s3;

        //c += (c.gbar-c)/3.+T(u-c.wz*t);
        //c += 0.125 * (0.5+0.5*sin(t));
    	//c += c.gbar/(sin(iTime*0.3)*8.)-c*.3+T(u-c.wz*t);
    	//c += c.gbar/(sin(iTime*0.3)*8.)-c*.3+T(u-c.yz*t);
    	//c += c.gbar/(cos(iTime*0.1)*8.)-c*.3+T(u-c.wz*t);
    	//c += c.gbar/(tan(iTime*0.1)*8.)-c*.3+T(u-c.wz*t);
    }

    float mixscl = 0.002 + 0.06*(0.5+0.5*sin(iTime*0.1));

	c = mix(T(u+c.xy), cos(c), mixscl);
}
